INSERT INTO post (post_content, post_user_id, post_thread_id) VALUES ('I am playing when it will come out', 1, 1),('no im not playing loser',2,1), ('Yes, I cant wait for the new update!', 2, 2), ('Me too! I hope they add some exciting features.', 4, 2), ('Im not really following the updates. Whats new?', 1, 2),('The developers have teased new characters and balancing changes.', 3, 2),('Looking forward to trying out the new characters!', 5, 2),('I find zoning strategies work well in online play.', 3, 3),('Agreed! Keeping a good spacing and controlling the match is key.', 4, 3),('What characters do you recommend for online play?', 1, 3),('Rushdown characters like Ryu and Cammy can be effective.', 2, 3),('Dont forget about strong defensive characters like Guile.', 5, 3), ('My favorite character is Ryu. Hadouken all the way!', 2, 2),('I love playing as Chun-Li. Her kicks are so powerful!', 3, 2),('Guile is my go-to character. Sonic boom!', 4, 2), ('Akuma is a beast! His Raging Demon move is insane!', 5, 2),('I cant wait to try out the new character, Rose!', 6, 2),('For Ryu, focus on zoning and utilizing his fireballs.', 1, 3),('I find that using quick characters like Cammy helps with aggressive playstyles.', 2, 3), ('Blocking and punishing is key. Dont forget to punish unsafe moves!', 4, 3),('Mixing up your attacks and throw game can catch opponents off guard.', 5, 3),('Learn the frame data! Understanding advantage and disadvantage is crucial.', 6, 3);;
